// City configuration - easily change the target city here
// Since fs module is not available in n8n browser environment, we'll use inline config

try {
  // Inline configuration instead of reading from file
  const config = {
    opentable_config: {
      current_city: 'new york',
      available_cities: ['new york', 'los angeles', 'chicago', 'san francisco', 'miami', 'boston', 'seattle', 'austin']
    }
  };

  const targetCity = config.opentable_config.current_city || 'new york';

  console.log(`ðŸŒ† Targeting city: ${targetCity}`);
  console.log(`ðŸ“‹ Available cities: ${config.opentable_config.available_cities.join(', ')}`);

  return [{
    json: {
      city: targetCity,
      encoded_city: encodeURIComponent(targetCity.replace(/ /g, '%20')),
      config_loaded: true,
      available_cities: config.opentable_config.available_cities,
      config_status: 'success'
    }
  }];

} catch (error) {
  console.log('âš ï¸ Could not load config, using default city');
  console.log('Error details:', error.message);

  return [{
    json: {
      city: 'new york',
      encoded_city: 'new%20york',
      config_loaded: false,
      error: error.message,
      config_status: 'failed',
      fallback_used: true
    }
  }];
}

// Improved restaurant extraction with multiple strategies and better error handling
let html = '';
let inputData = {};
try {
  inputData = items[0] && items[0].json ? items[0].json : {};
  html = inputData.data || inputData.body || inputData || '';

  if (typeof html !== 'string') {
    html = String(html);
  }

  console.log('HTML data received, length:', html.length, 'characters');
  console.log('Target city from input:', inputData.city || 'unknown');

  // Check if we got the wrong location
  const targetCity = (inputData.city || 'new york').toLowerCase();
  const wrongLocationKeywords = ['los angeles', 'atlanta', 'chicago', 'miami', 'boston', 'seattle'];
  const detectedWrongLocation = wrongLocationKeywords.find(city => 
    html.toLowerCase().includes(city) && city !== targetCity
  );

  if (detectedWrongLocation && !targetCity.includes(detectedWrongLocation)) {
    console.log(`WARNING: Expected ${targetCity} but detected ${detectedWrongLocation} in HTML`);
    return [{
      json: {
        error: 'Location mismatch detected',
        expected_city: targetCity,
        detected_city: detectedWrongLocation,
        html_sample: html.substring(0, 500)
      }
    }];
  }

} catch (error) {
  console.log('Error getting HTML data:', error.message);
  return [{ json: { error: 'Failed to get HTML data', details: error.message } }];
}

if (!html || html.length === 0) {
  console.log('No HTML data found');
  return [{ json: { error: 'No HTML data received', html_length: 0 } }];
}

// Check for access restrictions or blocks
if (html.includes('Access Denied') || html.includes('Blocked') || html.includes('Rate Limited') || html.includes('403 Forbidden')) {
  console.log('Access restricted detected');
  return [{ json: { error: 'Access restricted by website', html_sample: html.substring(0, 200) } }];
}

let restaurants = [];
let debugInfo = {
  strategies_attempted: [],
  html_contains_script: html.includes('<script'),
  html_contains_json: html.includes('application/json'),
  html_sample: html.substring(0, 500)
};

// Strategy 1: Try to extract from primary-window-vars script tag
try {
  debugInfo.strategies_attempted.push('primary-window-vars');
  console.log('Attempting Strategy 1: primary-window-vars script tag');

  const scriptMatch = html.match(/<script id="primary-window-vars" type="application\/json">(.*?)<\/script>/s);

  if (scriptMatch) {
    console.log('Found primary-window-vars script tag');
    const jsonContent = scriptMatch[1];

    if (jsonContent && jsonContent.length > 0) {
      const data = JSON.parse(jsonContent);
      console.log('Successfully parsed JSON from primary-window-vars');

      // Multiple paths to try for restaurant data
      const possiblePaths = [
        data?.windowVariables?.__INITIAL_STATE__?.multiSearch?.restaurants,
        data?.windowVariables?.__APOLLO_STATE__?.restaurants,
        data?.windowVariables?.__INITIAL_STATE__?.restaurants,
        data?.multiSearch?.restaurants
      ];

      for (const restaurantData of possiblePaths) {
        if (restaurantData && Array.isArray(restaurantData) && restaurantData.length > 0) {
          console.log(`Found ${restaurantData.length} restaurants in data structure`);

          // Process first 5 restaurants for testing
          const limitedRestaurants = restaurantData.slice(0, 5);

          for (const restaurant of limitedRestaurants) {
            try {
              const restaurantObj = {
                restaurantId: restaurant.restaurantId || null,
                name: restaurant.name || 'Unknown',
                link: restaurant.urls?.profileLink?.link ? 
                  (restaurant.urls.profileLink.link.startsWith('http') ? 
                    restaurant.urls.profileLink.link : 
                    'https://www.opentable.com' + restaurant.urls.profileLink.link) : '',
                cuisine: restaurant.primaryCuisine?.name || 'Unknown',
                rating: restaurant.statistics?.reviews?.ratings?.overall?.rating || 0,
                reviewCount: restaurant.statistics?.reviews?.allTimeTextReviewCount || 0,
                priceBand: restaurant.priceBand?.name || 'Unknown',
                neighborhood: restaurant.neighborhood?.name || 'Unknown',
                address: restaurant.address ? 
                  [restaurant.address.line1, restaurant.address.city, restaurant.address.state]
                    .filter(Boolean).join(', ') : 'Unknown',
                phone: restaurant.contactInformation?.formattedPhoneNumber || '',
                imageUrl: restaurant.photos?.profileV3?.medium?.url ? 
                  (restaurant.photos.profileV3.medium.url.startsWith('http') ? 
                    restaurant.photos.profileV3.medium.url : 
                    'https:' + restaurant.photos.profileV3.medium.url) : '',
                description: (restaurant.description || '').substring(0, 500),
                coordinates: {
                  latitude: restaurant.coordinates?.latitude || null,
                  longitude: restaurant.coordinates?.longitude || null
                },
                features: {
                  bar: restaurant.features?.bar || false,
                  outdoor: restaurant.features?.outdoor || false,
                  counter: restaurant.features?.counter || false,
                  highTop: restaurant.features?.highTop || false
                },
                hasTakeout: restaurant.hasTakeout || false,
                isPromoted: restaurant.isPromoted || false,
                topReview: restaurant.topReview?.highlightedText || '',
                scraped_at: new Date().toISOString(),
                source: 'OpenTable',
                search_city: inputData.city || 'new york',
                search_query: inputData.city || 'new york',
                extraction_method: 'JSON parsing - primary-window-vars',
                url_used: html.includes('Los Angeles') ? 'los-angeles-detected' : 'new-york-intended'
              };\

              restaurants.push(restaurantObj);

            } catch (error) {
              console.log('Error processing individual restaurant:', error.message);
            }
          }

          if (restaurants.length > 0) {
            break;
          }
        }
      }
    }
  }
} catch (error) {
  console.log('Strategy 1 failed:', error.message);
}

// Final results
if (restaurants.length > 0) {
  console.log(`Successfully extracted ${restaurants.length} restaurants`);
  console.log('Sample restaurant:', JSON.stringify(restaurants[0], null, 2));
  return restaurants.map(r => ({ json: r }));
} else {
  console.log('No restaurants found with any strategy');
  return [{
    json: {
      error: 'No restaurants extracted',
      debug_info: debugInfo,
      strategies_attempted: debugInfo.strategies_attempted,
      html_length: html.length,
      contains_restaurant_text: html.toLowerCase().includes('restaurant'),
      contains_opentable_text: html.toLowerCase().includes('opentable')
    }
  }];
}

// Debug and validate extracted data
const results = items || [];

console.log(`Received ${results.length} items from extraction`);

if (results.length === 0) {
  console.log('No items received from previous node');
  return [{ json: { error: 'No data received from extraction node' } }];
}

// Check if we got error information
const firstItem = results[0];
if (firstItem.json && firstItem.json.error) {
  console.log('Extraction failed with error:', firstItem.json.error);
  console.log('Debug info:', JSON.stringify(firstItem.json.debug_info || {}, null, 2));
  return results;
}

// Filter valid restaurants
const validRestaurants = results.filter(item => {
  return item.json && 
         item.json.name && 
         item.json.name !== 'Unknown' && 
         item.json.name.length > 0 &&
         !item.json.error;
});

console.log(`Found ${validRestaurants.length} valid restaurants out of ${results.length} total items`);

if (validRestaurants.length === 0) {
  console.log('No valid restaurants found. First item details:');
  console.log(JSON.stringify(firstItem, null, 2));
  return [{
    json: {
      error: 'No valid restaurants after filtering',
      total_items: results.length,
      sample_item: firstItem.json || firstItem
    }
  }];
}

// Process valid restaurants
const processedRestaurants = validRestaurants.map((item, index) => {
  const restaurant = item.json;

  return {
    ...restaurant,
    processed_at: new Date().toISOString(),
    processing_order: index + 1,
    data_quality: {
      has_name: !!restaurant.name,
      has_link: !!restaurant.link,
      has_rating: !!restaurant.rating,
      has_cuisine: !!restaurant.cuisine,
      has_address: !!restaurant.address
    }
  };
});

console.log('Successfully processed restaurants:');
processedRestaurants.forEach((r, i) => {
  console.log(`${i + 1}. ${r.name} (${r.cuisine}) - ${r.extraction_method}`);
});

return processedRestaurants.map(restaurant => ({ json: restaurant }));
