// Extract restaurant data from OpenTable HTML with enhanced error handling
let html = '';
try {
  const inputData = items[0] && items[0].json ? items[0].json : {};

  // Check for API errors first
  if (inputData.error || inputData.message) {
    console.log('ScrapingBee API Error:', inputData.error || inputData.message);
    return [{ json: { error: 'API Error', details: inputData.error || inputData.message, api_response: inputData } }];
  }

  // Check for rate limiting or blocking
  if (inputData.status && inputData.status !== 200) {
    console.log('Non-200 status:', inputData.status);
    return [{ json: { error: 'HTTP Error', status: inputData.status, details: 'Non-200 response from target site' } }];
  }

  html = inputData.data || inputData.body || inputData || '';
  if (typeof html !== 'string') {
    html = String(html);
  }

  console.log('HTML data received, length:', html.length, 'characters');

  // Check for blocked content
  if (html.includes('Access Denied') || html.includes('Blocked') || html.includes('Rate Limited')) {
    console.log('Access blocked by target site');
    return [{ json: { error: 'Access Blocked', details: 'Target site blocked the request', html_sample: html.substring(0, 500) } }];
  }

} catch (error) {
  console.log('Error getting HTML data:', error.message);
  return [{ json: { error: 'Failed to get HTML data', details: error.message } }];
}

if (!html || html.length === 0) {
  console.log('No HTML data found');
  return [{ json: { error: 'No HTML data received' } }];
}

let restaurants = [];

// Extract from primary-window-vars script tag
try {
  console.log('Attempting to extract restaurant data from JSON');

  const scriptMatch = html.match(/<script id="primary-window-vars" type="application\/json">(.*?)<\/script>/s);

  if (scriptMatch) {
    console.log('Found primary-window-vars script tag');
    const jsonContent = scriptMatch[1];

    if (jsonContent && jsonContent.length > 0) {
      const data = JSON.parse(jsonContent);
      console.log('Successfully parsed JSON from primary-window-vars');

      // Try different possible paths for restaurant data
      const possiblePaths = [
        data?.windowVariables?.__INITIAL_STATE__?.multiSearch?.restaurants,
        data?.windowVariables?.__APOLLO_STATE__?.restaurants,
        data?.windowVariables?.__INITIAL_STATE__?.restaurants,
        data?.multiSearch?.restaurants
      ];

      for (const restaurantData of possiblePaths) {
        if (restaurantData && Array.isArray(restaurantData) && restaurantData.length > 0) {
          console.log(`Found ${restaurantData.length} restaurants in data structure`);

          // Process up to 10 restaurants
          const limitedRestaurants = restaurantData.slice(0, 10);

          for (const restaurant of limitedRestaurants) {
            try {
              const restaurantObj = {
                restaurant_id: restaurant.restaurantId || null,
                name: restaurant.name || 'Unknown',
                link: restaurant.urls?.profileLink?.link ? 
                  (restaurant.urls.profileLink.link.startsWith('http') ? 
                    restaurant.urls.profileLink.link : 
                    'https://www.opentable.com' + restaurant.urls.profileLink.link) : '',
                cuisine: restaurant.primaryCuisine?.name || 'Unknown',
                rating: restaurant.statistics?.reviews?.ratings?.overall?.rating || 0,
                review_count: restaurant.statistics?.reviews?.allTimeTextReviewCount || 0,
                price_band: restaurant.priceBand?.name || 'Unknown',
                neighborhood: restaurant.neighborhood?.name || 'Unknown',
                address: restaurant.address ? 
                  [restaurant.address.line1, restaurant.address.city, restaurant.address.state]
                    .filter(Boolean).join(', ') : 'Unknown',
                phone: restaurant.contactInformation?.formattedPhoneNumber || '',
                image_url: restaurant.photos?.profileV3?.medium?.url ? 
                  (restaurant.photos.profileV3.medium.url.startsWith('http') ? 
                    restaurant.photos.profileV3.medium.url : 
                    'https:' + restaurant.photos.profileV3.medium.url) : '',
                description: (restaurant.description || '').substring(0, 500),
                latitude: restaurant.coordinates?.latitude || null,
                longitude: restaurant.coordinates?.longitude || null,
                has_bar: restaurant.features?.bar || false,
                has_outdoor: restaurant.features?.outdoor || false,
                has_takeout: restaurant.hasTakeout || false,
                is_promoted: restaurant.isPromoted || false,
                top_review: restaurant.topReview?.highlightedText || '',
                scraped_at: new Date().toISOString(),
                source: 'OpenTable'
              };

              restaurants.push(restaurantObj);

            } catch (error) {
              console.log('Error processing individual restaurant:', error.message);
            }
          }

          if (restaurants.length > 0) {
            break;
          }
        }
      }
    }
  }
} catch (error) {
  console.log('JSON extraction failed:', error.message);
}

// Return results
if (restaurants.length > 0) {
  console.log(`Successfully extracted ${restaurants.length} restaurants`);
  return restaurants.map(r => ({ json: r }));
} else {
  console.log('No restaurants found');
  return [{
    json: {
      error: 'No restaurants extracted',
      html_length: html.length,
      contains_restaurant_text: html.toLowerCase().includes('restaurant'),
      html_sample: html.substring(0, 500)
    }
  }];
}

// Summary of results
const results = items || [];
const successful = results.filter(item => !item.json.error);
const errors = results.filter(item => item.json.error);

console.log(`Processing complete:`);
console.log(`- Successful saves: ${successful.length}`);
console.log(`- Errors: ${errors.length}`);

if (successful.length > 0) {
  console.log('Successfully saved restaurants:');
  successful.forEach((item, i) => {
    console.log(`${i + 1}. ${item.json.name} (${item.json.cuisine})`);
  });
}

if (errors.length > 0) {
  console.log('Errors encountered:');
  errors.forEach((item, i) => {
    console.log(`${i + 1}. ${item.json.error}`);
  });
}

return [{
  json: {
    summary: {
      total_processed: results.length,
      successful_saves: successful.length,
      errors: errors.length,
      timestamp: new Date().toISOString()
    },
    restaurants_saved: successful.map(item => ({
      name: item.json.name,
      cuisine: item.json.cuisine,
      rating: item.json.rating
    }))
  }
}];
